$(function(){$(".userdeletebtn").click(function(){Swal.fire({title:"Potwierdź usunięcie",text:"Czy jesteś pewien, że chcesz usunąć wybrany rekord?",icon:"warning",showCancelButton:!0,confirmButtonColor:"#FF0000",cancelButtonColor:"#757575",confirmButtonText:"Tak, usuń!",cancelButtonText:"Nie, powrót"}).then(e=>{e.isConfirmed&&$.ajax({method:"DELETE",url:deleteUrl+$(this).data("id"),headers:{"X-CSRF-TOKEN":$('meta[name="csrf-token"]').attr("content")}}).done(function(t){window.location.reload()}).fail(function(t){Swal.fire({icon:"error",title:"Oops...",text:"Coś poszło nie tak!",footer:"Otrzymałeś błąd "+t.status})})})})});
